
/**
 * Module dependencies.
 */

var pause = require('pause');

/**
 * Delay `ms`.
 *
 * @param {Number} ms
 * @return {Function}
 * @api public
 */

module.exports = function(ms){
  ms = parseInt(ms, 10);
  console.log('  delaying %sms', ms);
  return function(req, res, next){
    var p = pause(req);
    setTimeout(function(){
      next();
      p.resume();
    }, ms);
  }
};